mapper.select:
SELECT * FROM t_knowledge_document
WHERE kb_id = #{knowledgeBaseId}
ORDER BY category_id, id
LIMIT #{pageNum}, #{pageSize}

SELECT d.* FROM t_operation_log ol JOIN t_knowledge_document d ON d.id = ol.object_id WHERE ol.operation_type = 4 AND (ol.object_type IS NULL OR ol.object_type = 'document') AND d.del_status = 0 AND d.doc_status = 1
AND ol.kb_id = #{kbId}
AND ol.created_by = #{userId}
GROUP BY d.id ORDER BY MAX(ol.created_at) DESC LIMIT #{pageNum}, #{pageSize}

SELECT COUNT(DISTINCT ol.object_id) FROM t_operation_log ol JOIN t_knowledge_document d ON d.id = ol.object_id WHERE ol.operation_type = 4 AND (ol.object_type IS NULL OR ol.object_type = 'document') AND d.del_status = 0 AND d.doc_status = 1
AND ol.kb_id = #{kbId}
AND ol.created_by = #{userId}
