mapper.select:
SELECT * FROM t_knowledge_document
WHERE kb_id = #{knowledgeBaseId}
ORDER BY category_id, id
LIMIT #{pageNum}, #{pageSize}

SELECT d.*, kb.title AS kbTitle FROM ( SELECT ol.object_id AS doc_id, MAX(ol.created_at) AS last_edit_at FROM t_operation_log ol WHERE ol.operation_type = 2
AND ol.kb_id = #{kbId}
GROUP BY ol.object_id ) le JOIN t_knowledge_document d ON d.id = le.doc_id JOIN t_knowledge_base kb ON kb.id = d.kb_id WHERE d.del_status = 0 AND d.doc_status = 1
AND d.kb_id = #{kbId}
AND NOT EXISTS ( SELECT 1 FROM t_operation_log ol2 WHERE ol2.object_id = d.id
AND ol2.kb_id = #{kbId}
AND ol2.operation_type = 5 AND ol2.created_at >= le.last_edit_at ) ORDER BY le.last_edit_at DESC LIMIT #{pageNum}, #{pageSize}

SELECT COUNT(1) FROM ( SELECT le.doc_id FROM ( SELECT ol.object_id AS doc_id, MAX(ol.created_at) AS last_edit_at FROM t_operation_log ol WHERE ol.operation_type = 2
AND ol.kb_id = #{kbId}
GROUP BY ol.object_id ) le JOIN t_knowledge_document d ON d.id = le.doc_id WHERE d.del_status = 0 AND d.doc_status = 1
AND d.kb_id = #{kbId}
AND NOT EXISTS ( SELECT 1 FROM t_operation_log ol2 WHERE ol2.object_id = d.id
AND ol2.kb_id = #{kbId}
AND ol2.operation_type = 5 AND ol2.created_at >= le.last_edit_at ) ) t

SELECT d.* FROM t_operation_log ol JOIN t_knowledge_document d ON d.id = ol.object_id WHERE ol.operation_type = 4 AND (ol.object_type IS NULL OR ol.object_type = 'document') AND d.del_status = 0 AND d.doc_status = 1
AND ol.kb_id = #{kbId}
AND ol.created_by = #{userId}
GROUP BY d.id ORDER BY MAX(ol.created_at) DESC LIMIT #{pageNum}, #{pageSize}

SELECT COUNT(DISTINCT ol.object_id) FROM t_operation_log ol JOIN t_knowledge_document d ON d.id = ol.object_id WHERE ol.operation_type = 4 AND (ol.object_type IS NULL OR ol.object_type = 'document') AND d.del_status = 0 AND d.doc_status = 1
AND ol.kb_id = #{kbId}
AND ol.created_by = #{userId}
